 

window.onresize = function() {

    var slidesPerView_Course = 0
    var slidesPerViewX_Instructor = 0;

    if (window.innerWidth >= 768) {

        slidesPerViewX_Instructor = 4;
        slidesPerView_Course = 4
    
     }
    if (window.innerWidth <= 667) {  

        slidesPerViewX_Instructor = 1;
        slidesPerView_Course = 2

     }
   
     var swiper = new Swiper('.swiper-course', {
        slidesPerView: slidesPerView_Course,
        spaceBetween: 30,
        navigation: {
      nextEl: '.swiper-button-next',
      prevEl: '.swiper-button-prev',
    },
      }); 


        var swiper = new Swiper('.swiper-instructor', {
          slidesPerView: slidesPerViewX_Instructor,
          spaceBetween: 30,
        });

    }

 